import { EntityService, type IEntitiesService } from '@kinotic/structures-api'
import { Dashboard } from '../../domain/Dashboard.js'


/**
 * Base Service for interacting with Dashboard entities
 * This class was generated by the Structures CLI. And will be overwritten if the CLI is run again.
 */
export class BaseDashboardEntityService extends EntityService<Dashboard> {

  private readonly shouldValidate: boolean

  constructor(shouldValidate: boolean = true, entitiesService?: IEntitiesService) {
    super('structures__system', 'Dashboard', entitiesService)
    this.shouldValidate = shouldValidate
  }

  protected async beforeSaveOrUpdate(entity: Dashboard): Promise<Dashboard> {
    if (this.shouldValidate) {
      return this.validate(entity)
    } else {
      return entity
    }
  }

  protected async beforeBulkSaveOrUpdate(entities: Dashboard[]): Promise<Dashboard[]> {
    if (this.shouldValidate) {
      const validatedEntities: Dashboard[] = []
      for (let entity of entities) {
        validatedEntities.push(this.validate(entity))
      }
      return validatedEntities
    } else {
      return entities
    }
  }

  validate(entity: Dashboard): Dashboard {
    let ret: any
    if (entity) {
      ret = (ret ? ret : {})
      ret.id = entity.id
      ret.name = entity.name
      ret.description = entity.description
      ret.applicationId = entity.applicationId
      ret.layout = entity.layout
      ret.created = entity.created
      ret.updated = entity.updated
    }

    return ret
  }

}
