import { EntityService, IEntitiesService } from '@kinotic/structures-api'
import { Address } from '../../../entity/domain/health/Address.js'


/**
 * Base Service for interacting with Address entities
 * This class was generated by the Structures CLI. And will be overwritten if the CLI is run again.
 */
export class BaseAddressEntityService extends EntityService<Address> {

  private readonly shouldValidate: boolean

  constructor(shouldValidate: boolean = true, entitiesService?: IEntitiesService) {
    super('healthcare', 'Address', entitiesService)
    this.shouldValidate = shouldValidate
  }

  protected async beforeSaveOrUpdate(entity: Address): Promise<Address> {
    if (this.shouldValidate) {
      return this.validate(entity)
    } else {
      return entity
    }
  }

  protected async beforeBulkSaveOrUpdate(entities: Address[]): Promise<Address[]> {
    if (this.shouldValidate) {
      const validatedEntities: Address[] = []
      for (let entity of entities) {
        validatedEntities.push(this.validate(entity))
      }
      return validatedEntities
    } else {
      return entities
    }
  }

  validate(entity: Address): Address {
    let ret: any
    if (entity) {
      ret = (ret ? ret : {})
      ret.id = entity.id
      ret.streetAddress = entity.streetAddress
      ret.city = entity.city
      ret.state = entity.state
      ret.postalCode = entity.postalCode
      ret.country = entity.country
      ret.isPrimary = entity.isPrimary
    }

    return ret
  }

}
