import { EntityService, IEntitiesService } from '@kinotic/structures-api'
import { ContactInfo } from '../../../entity/domain/health/ContactInfo.js'


/**
 * Base Service for interacting with ContactInfo entities
 * This class was generated by the Structures CLI. And will be overwritten if the CLI is run again.
 */
export class BaseContactInfoEntityService extends EntityService<ContactInfo> {

  private readonly shouldValidate: boolean

  constructor(shouldValidate: boolean = true, entitiesService?: IEntitiesService) {
    super('healthcare', 'ContactInfo', entitiesService)
    this.shouldValidate = shouldValidate
  }

  protected async beforeSaveOrUpdate(entity: ContactInfo): Promise<ContactInfo> {
    if (this.shouldValidate) {
      return this.validate(entity)
    } else {
      return entity
    }
  }

  protected async beforeBulkSaveOrUpdate(entities: ContactInfo[]): Promise<ContactInfo[]> {
    if (this.shouldValidate) {
      const validatedEntities: ContactInfo[] = []
      for (let entity of entities) {
        validatedEntities.push(this.validate(entity))
      }
      return validatedEntities
    } else {
      return entities
    }
  }

  validate(entity: ContactInfo): ContactInfo {
    let ret: any
    if (entity) {
      ret = (ret ? ret : {})
      ret.id = entity.id
      ret.email = entity.email
      ret.phoneNumber = entity.phoneNumber
      ret.emergencyContactName = entity.emergencyContactName
      ret.emergencyContactPhone = entity.emergencyContactPhone
      ret.emergencyContactRelationship = entity.emergencyContactRelationship
      ret.preferredContactMethod = entity.preferredContactMethod
      ret.isPrimary = entity.isPrimary
    }

    return ret
  }

}
