import { EntityService, IEntitiesService } from '@kinotic/structures-api'
import { Qualification } from '../../../entity/domain/health/Qualification.js'


/**
 * Base Service for interacting with Qualification entities
 * This class was generated by the Structures CLI. And will be overwritten if the CLI is run again.
 */
export class BaseQualificationEntityService extends EntityService<Qualification> {

  private readonly shouldValidate: boolean

  constructor(shouldValidate: boolean = true, entitiesService?: IEntitiesService) {
    super('healthcare', 'Qualification', entitiesService)
    this.shouldValidate = shouldValidate
  }

  protected async beforeSaveOrUpdate(entity: Qualification): Promise<Qualification> {
    if (this.shouldValidate) {
      return this.validate(entity)
    } else {
      return entity
    }
  }

  protected async beforeBulkSaveOrUpdate(entities: Qualification[]): Promise<Qualification[]> {
    if (this.shouldValidate) {
      const validatedEntities: Qualification[] = []
      for (let entity of entities) {
        validatedEntities.push(this.validate(entity))
      }
      return validatedEntities
    } else {
      return entities
    }
  }

  validate(entity: Qualification): Qualification {
    let ret: any
    if (entity) {
      ret = (ret ? ret : {})
      ret.id = entity.id
      ret.degree = entity.degree
      ret.institution = entity.institution
      ret.certification = entity.certification
      ret.yearObtained = entity.yearObtained
      ret.isVerified = entity.isVerified
      ret.verificationDate = entity.verificationDate
      ret.expiryDate = entity.expiryDate
      ret.isActive = entity.isActive
    }

    return ret
  }

}
